<div class="dialog-bg" id="dialog_bg"></div>
<div class="transform-bg" id="transform_bg"></div>

<div class="confirm" id="sarah_confirm">
    <div class="title float-clear">
        <i class="iconfont iconWarning float-left"></i>
        <div class="float-left">
            <div class="content"></div>
            <div class="tips"></div>
        </div>
    </div>
    <div class="float-right margin-top-v5">
        <button class="btn btn-cancel btn-xs letter-space margin-right-v2" onclick="confirmHide()">取消</button>
        <button class="btn btn-blue btn-xs letter-space" id="save_confirm">确定</button>
    </div>
</div>

<div class="toast-success float-clear" id="toast_success">
    <i class="iconfont iconSuccess float-left"></i>
    <span class="float-left content"></span>
</div>

<div class="toast-error float-clear" id="toast_error">
    <i class="iconfont iconError float-left"></i>
    <span class="float-left content"></span>
</div>

<div class="toast-warning float-clear" id="toast_warning">
    <i class="iconfont iconWarning float-left"></i>
    <span class="float-left content"></span>
</div>












